mapper.select:
SELECT * FROM cart 
WHERE cart_item_no = #{cartItemNo} 
  AND users_id = #{usersId} 
  AND volume_no = #{volumeNo}

mapper.update:
UPDATE cart 
SET cart_qty = #{cartQty} 
WHERE users_id = #{usersId} AND cart_no = #{cartNo}

mapper.delete:
DELETE FROM cart
WHERE cart_no IN

  #{cartNo}

AND users_id = #{usersId}
